package com.arvind.GettingStarted;import java.util.Scanner;/*For example:Sample Input:5132345Sample Output:primeprimeprimenot primeprime* */public class PrimeNumber {    public static void main(String[] args) {        Scanner scn = new Scanner(System.in);        int t = scn.nextInt();        for (int i = 0; i < t; i++) {            int n = scn.nextInt();            primeNumebr(9);        }    }    public  static void primeNumebr(int n){        int count=0;        for (int i = 1; i <=n; i++) {            if(n%i==0){                count++;            }        }        if(count==2){            System.out.println("This is the prime");        }else{            System.out.println("This is not prime");        }    }}